INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9768, 'Clarithromycin', 'Trastuzumab emtansine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to the cytotoxic component of ado-trastuzumab emtansine known as DM1, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 3A5.', NULL, 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, 'The use of ado-trastuzumab emtansine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of ado-trastuzumab emtansine during and for 2 weeks after treatment with itraconazole. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider delaying initiation of ado-trastuzumab emtansine until therapy with the potent CYP450 3A4 inhibitor is complete and the drug has cleared from the circulation, or approximately 3 elimination half-lives. When concomitant administration is necessary, patients should be closely monitored for adverse reactions such as hepatic impairment, left ventricular dysfunction, peripheral neuropathy, and thrombocytopenia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9772/', '[1] "Product Information. Kadcyla (ado-trastuzumab)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9769, 'Clofarabine', 'Trastuzumab emtansine', 'Moderate', 'The liver is a known target organ for clofarabine toxicity, and concomitant use of other potentially hepatotoxic agents may increase the risk of liver injury. Severe and fatal hepatotoxicity has occurred with the use of clofarabine alone.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'Concomitant use of clofarabine with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Hepatic function should be monitored during clofarabine administration, and therapy discontinued if grade 3 to 4 liver enzyme or bilirubin elevations occur. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9773/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767365203);
