INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9976, 'Epinephrine', 'Deserpidine', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the pharmacologic effects of direct-acting sympathomimetic amines, while the hypotensive effect of the former may be diminished. Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings, thus they may sensitize adrenergic receptors to direct-acting sympathomimetics.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Conversely, alternatives to postganglionic adrenergic blocking agents should be considered in patients treated with sympathomimetic amines. If the combination is used, blood pressure and heart rate should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9980/', '[1] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[2] Muelheims GH, Entrup RW, Paiewonsky D, Mierzwiak DS "Increased sensitivity of the heart to catecholamine-induced arrhythmias following guanethidine." Clin Pharmacol Ther 6 (1965): 757-62[3] Spiers AS, Calne DB "Action of dopamine on the human iris." Br Med J 4 (1969): 333-5', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9977, 'Epinephrine', 'Desflurane', 'Major', 'Rarely, the combination of epinephrine and some anesthetics has resulted in ventricular irritability, serious cardiac arrhythmias, or death. The halogenated anesthetics sensitize the heart to the arrhythmogenic effects of catecholamines.', NULL, 'This combination should be avoided or used very cautiously.', NULL, 'This combination should be avoided or used very cautiously. If a halogenated anesthetic and epinephrine must be used together, an epinephrine concentration of 1:100,000 or 1:200,000 is recommended. The adult dosage should not exceed 100 mcg of a 1:100,000 solution in any 10-minute period. The patient''s hemodynamic status should be closely monitored. Lidocaine has a protective effect against the arrhythmogenic potential of epinephrine. Topical epinephrine appears to be relatively safe for use during anesthesia.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9981/', '[1] Karl HW, Swedlow DB, Lee KW, Downes JJ "Epinephrine-halothane interactions in children." Anesthesiology 58 (1983): 142-5[2] Konchigeri HN, Shaker MH, Winnie AP "Effect of epinephrine during enflurane anesthesia." Anesth Analg 53 (1974): 894-7[3] "Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical, Abbott Park, IL.[4] Reisner LS, Lippmann M "Further data on epinephrine interactions." Anesth Analg 56 (1977): 468[5] Mostello L, Stueber K, Lake CR "Is topical application of epinephrine at skin graft donor sites during halothane anesthesia safe?" Ann Plast Surg 20 (1988): 313-6[6] Bennett JA, Eltringham RJ "Possible dangers of anaesthesia in patients receiving 