INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10100, 'Epinephrine', 'Reserpine', 'Moderate', 'Postganglionic adrenergic blocking agents may potentiate the pharmacologic effects of direct-acting sympathomimetic amines, while the hypotensive effect of the former may be diminished. Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings, thus they may sensitize adrenergic receptors to direct-acting sympathomimetics.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', NULL, 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Conversely, alternatives to postganglionic adrenergic blocking agents should be considered in patients treated with sympathomimetic amines. If the combination is used, blood pressure and heart rate should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10104/', '[1] Hardman JG, Gilman AG, Limbird LE eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 9th ed." New York, NY: McGraw-Hill (1995):[2] Muelheims GH, Entrup RW, Paiewonsky D, Mierzwiak DS "Increased sensitivity of the heart to catecholamine-induced arrhythmias following guanethidine." Clin Pharmacol Ther 6 (1965): 757-62[3] Spiers AS, Calne DB "Action of dopamine on the human iris." Br Med J 4 (1969): 333-5', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10101, 'Epinephrine', 'Risperidone', 'Moderate', 'Phenothiazines and other neuroleptics may inhibit or reverse the pressor effect of adrenaline (epinephrine), dopamine, and similar vasoconstrictors. Many of these agents, including the atypical antipsychotics, exhibit alpha-1 adrenergic blocking activity and produce hypotension as an adverse effect. Use of adrenaline or dopamine for drug-induced hypotension and circulatory collapse in patients receiving neuroleptic therapy may cause a paradoxical further lowering of blood pressure, since beta stimulation may worsen hypotension in the setting of alpha blockade.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents.', NULL, 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents. Alternative vasopressor agents such as metaraminol, noradrenaline (norepinephrine), or phenylephrine should be considered, and vital signs closely monitored.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10105/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Loxitane C Oral Concentrate (loxapine)" Watson Laboratories Inc, Corona, CA.[4] Sletten IW, Lang WJ, Brown ML, Ballou SR, Gershon S "Chronic chlorpromazine administratio