INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10112, 'Epinephrine', 'Solriamfetol', 'Moderate', 'Additive or synergistic effects on blood pressure and/or heart rate may occur when solriamfetol is combined with other drugs that increase blood pressure and/or heart rate. The use of solriamfetol alone has been associated with dose-dependent increases in blood pressure and heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate. Blood pressure should be monitored prior to initiating solriamfetol therapy and regularly during treatment. In addition, hypertension should be controlled prior to treatment, and any new-onset hypertension or exacerbations of pre-existing hypertension that occur during solriamfetol therapy should be treated. Discontinuation of solriamfetol should be considered in patients who experience an increase in blood pressure or heart rate that cannot be managed with dose reduction of solriamfetol or other appropriate medical intervention.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10116/', '[1] "Product Information. Sunosi (solriamfetol)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10113, 'Epinephrine', 'Sotalol', 'Major', 'Noncardioselective beta-blockers can significantly enhance the pressor response to epinephrine. The mechanism involves blockade of beta-2 adrenergic receptors in the peripheral vasculature, resulting in unopposed alpha-adrenergic effect of epinephrine that is responsible for vasoconstriction. Beta-blockers may attenuate the response to epinephrine in the treatment of anaphylactic reactions. Noncardioselective beta-blockers, in particular, can antagonize the bronchodilating effects of epinephrine by blocking beta-2 adrenergic receptors in smooth muscles of the bronchial tree.', NULL, 'Extreme caution and close monitoring of cardiovascular status are indicated when epinephrine is administered to patients treated with noncardioselective beta-blockers.', NULL, 'Extreme caution and close monitoring of cardiovascular status are indicated when epinephrine is administered to patients treated with noncardioselective beta-blockers. A dosage reduction of epinephrine may be necessary. Withdrawal of beta-blockers before anesthesia may increase the risk of myocardial ischemia and is not recommended. The interaction is not expected to occur with local anesthetics used in dental surgery that contain very low concentrations of epinephrine.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10117/', '[1] Spencer PS, Klein LE "Interaction of epinephrine and propranolol." J Am Acad Dermatol 14 (1986): 1093-4[2] Richards DA, Prichard BN, Hernandez R "Circulatory effects of noradrenaline and adrenaline before and after labetalol." Br J Clin Pharmacol 7 (1979): 371-8[3] O