INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10203, 'Ibuprofen', 'Clopidogrel', 'Moderate', 'The coadministration of clopidogrel with naproxen resulted in occult gastrointestinal blood loss in healthy volunteers. The mechanism has not been described, but may be due to additive platelet inhibition. The coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and clopidogrel should be undertaken with extreme caution. Additionally, some NSAIDs are substrates for the CYP450 isoenzyme 2C9. Clopidogrel inhibits this isoenzyme and therefore may lead to decreased metabolism of these NSAIDs.', NULL, 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel.', NULL, 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel. Patients should also be advised to promptly report any signs of GI bleeding to their caregiver, including pain, swelling, dizziness, weakness, bloody or coffee-ground emesis, or red or black stools, and to avoid any over-the-counter NSAID products.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10207/', '[1] "Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb, Princeton, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10204, 'Ibuprofen', 'Clove', 'Minor', 'Clove and clove oil may potentiate the effects of anticoagulants and other drugs that may affect hemostasis such as platelet inhibitors, thrombolytic agents, and nonsteroidal anti-inflammatory drugs, possibly increasing the risk of bleeding. Eugenol, the active constituent of clove and clove oil, has been shown to be an inhibitor of platelet activity.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used clove and clove oil extensively prior to receiving treatment with anticoagulants or other drugs that may affect hemostasis, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10208/', '[1] Srivasta KC "Antiplatelet principles from a food spice clove (Syzygium aromaticum L) [corrected]" Prostaglandins Leukot Essent Fatty Acids 48 (1993): 363-72[2] Saeed SA, Gilani AH "Antithrombotic activity of clove oil." J Pak Med Assoc 44 (1994): 112-5[3] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alt