INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10334, 'Ibuprofen', 'Mifepristone', 'Moderate', 'Coadministration with mifepristone may increase the plasma concentrations of drugs that are substrates of CYP450 2C8 and/or 2C9. The mechanism is decreased clearance due to inhibition of CYP450 2C8/2C9 activity by mifepristone.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that are substrates of CYP450 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that are substrates of CYP450 2C8 and/or 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever mifepristone is added to or withdrawn from therapy. The lowest dosage of sensitive substrates (e.g., repaglinide, warfarin) should be used whenever possible. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10338/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767365203);
