INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10365, 'Ibuprofen', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10369/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10366, 'Ibuprofen', 'Pemetrexed', 'Moderate', 'INTERVAL: Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the plasma concentrations of pemetrexed. The mechanism has not been described but may be related to NSAID inhibition of renal prostaglandins. Use of NSAIDs has been associated with nephropathy manifested as elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure. Since pemetrexed is primarily eliminated unchanged by renal excretion, coadministration with NSAIDs may result in delayed and/or decreased clearance.', NULL, 'Ibuprofen at 400 mg and aspirin at 325 mg four times a day, or less, may be used with pemetrexed in patients with normal renal function.', NULL, 'Ibuprofen at 400 mg and aspirin at 325 mg four times a day, or less, may be used with pemetrexed in patients with normal renal function. However, caution is advised in patients with mild to moderate renal insufficiency (creatinine clearance 45 to 79 mL/min). These patients should avoid taking NSAIDs with short elimination half-lives (e.g., diclofenac, etodolac, fenoprofen, flurbiprofen, ibuprofen, indomethacin, ketoprofen, ketorolac, meclofenamate, mefenamic acid, sulindac, tolmetin, low dosages of salicylates) 2 days before to 2 days after pemetrexed administration. If concomitant administration is necessary, patients should be monitored closely for toxicity, especially myelosuppression, nephrotoxicity, and gastrointestinal toxicity.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10370/', '[1] Shand DG, Epstein C, Kinberg-Calhoun J, et al "The effect of etodolac administration on renal function in patients with arthritis." J Clin Pharmacol 26 (1986): 269-74[2] Tietjen DP "Recurrence and specificity of nephrotic syndrome due to tolmetin." Am J Med 87 (1989): 354-5[3] Turner GA, Walker RJ, Bailey RR, et al "Sulindac-induced acute interstitial nephritis." N Z Med J 97 (1984): 239-40[4] Lomvardias S, Pinn VW, Wadhwa ML, et al "Nephrotic syndrome associated with sulindac." N Engl J Med 304 (1981): 424[5] Tattersall J, Greenwood R, Farrington K "Membranous nephropathy associated with diclofenac ." Postgrad Med J 68 (1992): 392-3[6] Sarma PS "Fatal acute renal failure after piroxicam." Clin Nephrol 31 (1989): 54[7] McCarthy JT, Torres VE, Romero JC, et al "Acute intrinsic renal failure induced by indomethacin." Mayo Clin Proc 57 (1982): 289-96[8] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[9] Chan XM "Fatal renal failure due to indomethacin." Lancet 2 (1987): 340[10] Fellner SK "Piroxicam-induced acute interstitial nephritis and minimal-change nephrotic syndrome." Am J Nephrol 5 (1985): 142-3[11] Quan DJ, Kayser SR "Ketorolac induced acute renal failure following a single dose." J Toxicol Clin Toxicol 32 (1994): 305-9[12] Frais MA, Burgess ED, Mitchell LB "Piroxicam-induced renal failure and hyperkalemia." Ann Intern Med 99 (1983): 129-30[13] de Crespigny PJ, Becker GJ, Ihle BU, et al "Renal failure and nephrotic syndrome associated with sulindac." Clin Nephrol 30 (1988): 52-5[14] Pazmino PA, Pazmino PB "Ketoprofen-induced irreversible renal failure." Nephron 50 (1988): 70-1[15] Radford RG, Holley KE, Grande JP, Larson TS, Wagoner RD, Donadio JV, Mccarthy JT "Reversible membranous nephropathy associated with the use of nonsteroidal anti-inflammatory drugs." JAMA 276 (1996): 466-9[16] Feldman HI, Kinman JL, Berlin JA, et al. "Parenteral ketorolac: the risk for acute renal failure." Ann Intern Med 126 (1997): 193-9[17] Perneger TV