INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10453, 'Ibuprofen', 'Urokinase', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10457/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10454, 'Ibuprofen', 'Valdecoxib', 'Moderate', 'Concomitant use of COX-2 inhibitors with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation. The risk is dependent on both dosage and duration of therapy.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided. Some authorities consider the use of COX-2 inhibitors with non-aspirin NSAIDs as contraindicated.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10458/', '[1] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10455, 'Ibuprofen', 'Valproic acid', 'Minor', 'High doses of ibuprofen (greater than 400 mg/day) may displace valproic acid from protein binding sites in vitro. This interaction may be more significant in uremic patients. Free valproic acid concentrations should be measured if toxicity is suspected.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10459/', '[1] Dasgupta A, Volk A "Displacement of valproic acid and carbamazepine from protein binding in normal and uremic sera by tolmetin, ibuprofen, and naproxen: presence of inhibitor in uremic serum that blocks valproic acid-naproxen interactions." Ther Drug Monit 18 (1996): 284-7[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10456, 'Ibuprofen', 'Valsartan', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10460/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10457, 'Ibuprofen', 'Vancomycin', 'Moderate', 'When administered concomitantly, vancomycin and ketorolac may have additive nephrotoxic effects. Ketorolac, like other nonsteroidal anti-inflammatory drugs (NSAIDs), inhibits vasodilatory renal prostaglandin synthesis. In addition, chronic use of NSAIDs may also be associated with renal toxicities, including renal failure. The mechanism of vancomycin-induced nephrotoxicity is unknown; however, the risk may be increased in patients with preexisting risk factors for nephrotoxicity, high vancomycin blood levels, or prolonged treatment.', NULL, 'Caution is recommended in patients receiving ketorolac or other NSAIDs in combination with IV vancomycin, particularly in patients with preexisting risk factors for nephrotoxicity.', NULL, 'Caution is recommended in patients receiving ketorolac or other NSAIDs in combination with IV vancomycin, particularly in patients with preexisting risk factors for nephrotoxicity. Renal function and vancomycin therapeutic drug monitoring is recommended according to local policies and protocols. The dose and duration of concomitant NSAID therapy should also be minimized where possible.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10461/', '[1] Hazlewood KA, Brouse SD, Pitcher WD, Hall RG "Vancomycin-associated nephrotoxicity: grave concern or death by character assassination?" Am J Med 123 (2010): 182.e1-7[2] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[3] Murray RP, Watson RC "Acute renal failure and gastrointestinal bleed associated with postoperative toradol and vancomycin." Orthopedics 16 (1993): 1361-3[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Vancocin (vancomycin)." Lilly, Eli and Company, Indianapolis, IN.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10458, 'Ibuprofen', 'Vasopressin', 'Moderate', 'The antidiuretic response to vasopressin or desmopressin may be potentiated by certain drugs, including nonsteroidal anti-inflammatory agents, selective serotonin reuptake inhibitors, tricyclic/tetracyclic antidepressants, carbamazepine, chlorpropamide, chlorpromazine, clofibrate, eslicarbazepine, fludrocortisone, haloperidol, lamotrigine, oxcarbazepine, urea, and some antineoplastic agents (e.g., vinca alkaloids, cisplatin, cyclophosphamide). These drugs can occasionally cause fluid retention, in some cases secondary to the syndrome of inappropriate antidiuretic hormone secretion (SIADH). Coadministration with vasopressin or desmopressin may, therefore, increase the risk of water intoxication and/or hyponatremia. Seizure and coma in association with severe hyponatremia have been reported during concomitant use of desmopressin and imipramine or ibuprofen.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH.', NULL, 'Caution is advised when vasopressin or desmopressin is used in combination with drugs that can cause fluid retention or SIADH. Some authorities recommend adjusting vasopressin dosage as needed. Serum electrolytes, especially sodium, as well as BUN and creatinine should be monitored regularly. Patients should be advised to seek medical attention if they develop signs and symptoms of water intoxication or hyponatremia such as anorexia, nausea, vomiting, headache, malaise, lethargy, irritability, difficulty concentrating, memory impairment, confusion, weakness, muscle spasm, unsteadiness (which may lead to falls), anuria, and weight gain. Severe hyponatremia can be life-threatening if it is not promptly diagnosed and treated; therefore, early treatment is important to help prevent progression to seizures, coma, respiratory arrest, and death.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10462/', '[1] Vanhees SL, Paridaens R, Vansteenkiste JF "Syndrome of inappropriate antidiuretic hormone associated with chemotherapy-induced tumour lysis in small-cell lung cancer: case report and literature review." Ann Oncol 11 (2000): 1061-5[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Kosmidis HV, Bouhoutsou DO, Varvoutsi MC, et al. "Vincristine overdose: experience with 3 patients." Pediatr Hematol Oncol 8 (1991): 171-8[4] Maramattom BV "Duloxetine-induced syndrome of inappropriate antidiuretic hormone secretion and seizures." Neurology 66 (2006): 773-4[5] ten Holt WL, van Iperen CE, Schrijver G, Bartelink AK "Severe hyponatremia during therapy with fluoxetine." Arch Intern Med 156 (1996): 681-2[6] Miller MG "Tricyclics as a possible cause of hyponatremia in psychiatric patients." Am J Psychiatry 146 (1989): 807[7] Petersson I, Nilsson G, Hansson B-G, Hedner T "Water intoxication associated with non-steroidal anti-inflammatory drug therapy." Acta Med Scand 221 (1987): 221-3[8] Nielsen OA, Johannessen AC, Bardrum B "Oxcarbazepine-induced hyponatremia, a cross-sectional study." Epilepsy Res 2 (1988): 269-71[9] Schattner A, Skurnik Y "Fluoxetine-induced SIADH." J Am Geriatr Soc 44 (1996): 1413[10] Tanay A, Firemann Z, Yust I, Abramov AL "Chlorpropamide-induced syndrome of inappropriate antidiuretic hormone secretion." J Am Geriatr Soc 29 (1981): 334-6[11] Catalano G, Kanfer SN, Catalano MC, Alberts VA "The role of sertraline in a patient with recurrent hyponatremia." Gen Hosp Psychiatry 18 (1996): 278-83[12] Kokko H, Hall PD, Afrin LB "Fentanyl-associated syndrome of inappropriate antidiuretic hormone se