INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10467, 'Ibuprofen', 'Zanubrutinib', 'Major', 'Coadministration of zanubrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', NULL, 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', NULL, 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Discontinue zanubrutinib if intracranial hemorrhage of any grade occurs.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10471/', '[1] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10468, 'Ibuprofen', 'Zoledronic acid', 'Moderate', 'Theoretical concerns exist regarding the potential for increased risk and severity of gastrointestinal toxicity during coadministration of oral bisphosphonates and nonsteroidal anti-inflammatory drugs (NSAIDs) due to additive or synergistic irritant effects on the gastrointestinal mucosa. Because NSAIDs reduce the rate of ulcer healing in the stomach and duodenum, it is also possible that NSAIDs may delay healing and exaggerate the mucosal injury caused by oral bisphosphonates. Theoretical concerns exist regarding the potential for increased risk and severity of renal impairment during coadministration of bisphosphonates with high dosages or chronic use of NSAIDs due to additive or synergistic nephrotoxic effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure. Cases have primarily involved intravenous formulations of the drugs such as pamidronic acid and zoledronic acid, especially when they are administered too rapidly. The risk of hypocalcemia may also be increased, as drug-induced renal tubular damage can lead to renal loss of calcium and other electrolytes such as magnesium. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. Chronic use of nonsteroidal anti-inflammatory drugs (NSAIDs) may be associated with renal toxicities including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', NULL, 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs.', NULL, 'Caution is advised if bisphosphonates are prescribed in combination with NSAIDs. Patients receiving oral bisphosphonates should be closely monitored for the development of gastrointestinal toxicity and advised to immediately report potential signs and symptoms such as severe abdominal pain, nausea, vomiting, diarrhea, loss of appetite, dizziness, lightheadedness, and/or black, tarry stools. Patients receiving intravenous formulations of bisphosphonates should have renal function and serum electrolytes closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10472/', '[1] "Product Information. Actonel (risedronate)." Procter and Gamble Pharmaceuticals, Cincinnati, OH.[2] "Product Information. Bonefos (clodronate)." Rhone-Poulenc Rorer Canada Inc, Laval, QC.[3] "Product Information. Didronel I.V. Infusion (etidronate)." MGI Pharma Inc, Minnetonka, MN.[4] Graham, Malaty "Alendronate and naproxen are synergistic for development of gastric ulcers (Vol 161, pg 107, 1921)." Arch Intern Med 161 (2001): 1862[5] Graham DY, Malaty HM "Alendronate and naproxen are synergistic for development of gastric ulcers." Arch Intern Med 161 (2001): 107-10[6] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[7] "Product Information. Fosamax (alendronate)." Merck & Co, Inc, West Point, PA.[8] "Product Information. Aredia (pamidronate)." Novartis Pharmaceuticals, East Hanover, NJ.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Didronel Tablets (etidronate)." Procter and Gamble Pharmaceuticals, Cincinnati, OH.[11] "Product Information. Boniva (ibandronate)." Roche Laboratories, Nutley, NJ.[12] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10469, 'Flunisolide', 'Salbutamol', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects. Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes. However, clinical data are limited, and the potential significance is unknown. Patients who are receiving systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or systemic corticosteroid therapy may be at a greater risk of developing hypokalemia.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10473/', '[1] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):', 1767365203);
