INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329742, 'Tramadol', 'Phentermine', 'Major', 'The risk of seizures may be increased during coadministration of tramadol with any substance that can reduce the seizure threshold, such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline. These agents are often individually epileptogenic and may have additive effects when combined. Many of these agents also exhibit CNS- and/or respiratory-depressant effects, which may be enhanced during their concomitant use with tramadol.', 'DDInter', 1767317475, 'Caution is advised if tramadol is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections).', 'Synergism', 'Caution is advised if tramadol is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, NULL, 'Major', 'pharmacodynamic');
