INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329793, 'Tramadol', 'Safinamide', 'Major', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the serotonergic activity of tramadol and increase the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors. The mechanism is an additive pharmacodynamic effect resulting from MAOI inhibition of serotonin metabolism. Patients receiving this combination of drugs may also experience an increased risk of seizures due to lowering of seizure threshold.', 'DDInter', 1767317475, 'Concurrent use of tramadol and MAOIs or other agents that possess MAOI activity (e.g., furazolidone, procarbazine) is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with tramadol.', 'Synergism', 'Concurrent use of tramadol and MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
