INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329833, 'Tramadol', 'Tiagabine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329834, 'Tramadol', 'Ticagrelor', 'Moderate', 'Coadministration with opioid agonists may delay and reduce the absorption of orally administered P2Y12 inhibitors (e.g., clopidogrel, prasugrel, ticagrelor). The proposed mechanism may involve opioid-mediated slowed gastric emptying.', 'DDInter', 1767317475, 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists. In acute coronary syndrome patients who require an opioid agonist, the use of a parenteral antiplatelet agent, such as cangrelor, should be considered.', 'Absorption', 'Although data are limited, caution is recommended when orally administered P2Y12 inhibitors are given concomitantly with opioid agonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
