INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329887, 'Amphotericin B', 'Acetazolamide', 'Moderate', 'The concomitant use of amphotericin and potassium-wasting diuretics may result in increased risk of hypokalemia due to additive effects.', 'DDInter', 1767317475, 'Patients receiving diuretics with amphotericin should be monitored closely for development of hypokalemia. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving diuretics with amphotericin should be monitored closely for development of hypokalemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
