INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329921, 'Clozapine', 'Acetazolamide', 'Moderate', 'One case report has suggested that combination of carbonic anhydrase inhibitors (CAIs) and clozapine may increase the risk of neutropenia. The case involved methazolamide, and the authors speculate that acetazolamide may participate in this interaction as well.', 'DDInter', 1767317475, 'Close monitoring is advisable during concomitant use. White blood cell (WBC) counts and absolute neutrophil counts (ANC) should be measured regularly according to official recommendations, and clozapine should be discontinued if cell counts fall below critical limits. Patients should be counseled to notify their doctor immediately if they experience influenza-like symptoms, fever, or a sore throat.', 'Synergism', 'Close monitoring is advisable during concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329922, 'Corticotropin', 'Acetazolamide', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 1767317475, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, NULL, 'Moderate', 'pharmacodynamic');
