INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329973, 'Hydroxychloroquine', 'Acetazolamide', 'Moderate', 'Chloroquine or hydroxychloroquine may lower the convulsive threshold, which may antagonize the action of antiepileptic medications. Seizures have been rarely reported in patients on chloroquine or hydroxychloroquine therapy. The mechanism is unknown.', 'DDInter', 1767317475, 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy. In addition, patients with a history of epilepsy should be advised about the risk of chloroquine/hydroxychloroquine-induced seizures. Alternative anti-malarial agents may be required. Local treatment guidelines should be consulted.', 'Synergism', 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1329974, 'Iloprost', 'Acetazolamide', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 1767317475, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
