INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21081, 'Amiloride', 'Polyethylene glycol (3350 with electrolytes)', 'Moderate', 'Coadministration with agents that affect renal function or perfusion such as diuretics, ACE inhibitors, angiotensin receptor blockers, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk and/or severity of fluid and electrolyte disturbances associated with the use of bowel cleansing preparations. Fluid and electrolyte disturbances can lead to serious adverse events including cardiac arrhythmias, seizures, and renal impairment.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.', NULL, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment. Baseline and postprocedure labs including serum electrolytes, BUN, and creatinine should be considered. Patients should be advised to drink sufficient quantities of clear fluids before, during, and after bowel cleansing. Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21096/', '[1] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories, Braintree, MA.[2] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories, Braintree, MA.[3] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[5] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Golytely (polyethylene glycol electrolyte solution)." Braintree, Braintree, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21082, 'Amiloride', 'Potassium acetate', 'Major', 'The concomitant use of potassium-sparing diuretics and potassium preparations may result in hyperkalemia.', NULL, 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended.', NULL, 'The use of potassium preparations in patients treated with potassium-sparing diuretics is generally not recommended. Caution is advised if coadministration is required, particularly in patients with renal impairment, diabetes, old age, worsening heart failure, and/or a risk for dehydration. Serum potassium and renal function should be checked regularly, and oral potassium supplementation should be avoided in patients with severe congestive heart failure who have serum potassium levels above 3.5 mEq/L. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Calcium chloride, Zinc sulfate, Magnesium sulfate, Sodium acetate, Arginine, Sodium glycerophosphate, Zinc chloride, Tromethamine, Sodium bicarbonate, Sodium chloride, Ammonium chloride, Magnesium chloride', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21097/', '[1] Shapiro S, Slone D, Lewis GP, Jick H "Fatal drug reactions among medical inpatients." JAMA 216 (1971): 467-72[2] McNay JL, Oran E "Possible predisposition of diabetic patients to hyperkalemia following administration of potassium-retaining diuretic, amiloride (MK 870)." Metabolism 19 (1970): 58-70[3] Walmsley RN, White GH, Cain M, McCarthy PJ, 