INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21151, 'Anistreplase', 'Aminocaproic acid', 'Moderate', 'Coadministration of an antifibrinolytic agent (e.g., aminocaproic acid, tranexamic acid, aprotinin) in combination with a tissue plasminogen activator (t-PA) may reduce the therapeutic effects of both drugs due to opposing pharmacodynamic actions. Aminocaproic acid and tranexamic acid are competitive inhibitors of plasminogen activation. At high concentrations they can also directly inhibit plasmin, which is needed for the degradation of fibrin and thrombolysis. Aprotinin is a broad-spectrum protease inhibitor that inhibits plasmin. It is known to have antifibrinolytic activity and may interact with t-PAs in a similar fashion.', NULL, 'No specific intervention is warranted, but clinicians should be alert to the potential for diminished therapeutic efficacy if a tissue plasminogen activator is administered to a patient who has been treated with an antifibrinolytic agent, and vice versa.', NULL, 'No specific intervention is warranted, but clinicians should be alert to the potential for diminished therapeutic efficacy if a tissue plasminogen activator is administered to a patient who has been treated with an antifibrinolytic agent, and vice versa.', 'Antagonism', 'Betrixaban, Clopidogrel, Cilostazol, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Cangrelor, Dipyridamole, Caplacizumab, More', 'Aprotinin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21166/', '[1] "Product Information. LYSTEDA (tranexamic acid)." Xanodyne Pharmaceuticals Inc, Newport, KY.[2] "Product Information. Cyklokapron (tranexamic acid)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Product Information. Trasylol (aprotinin)." Bayer, West Haven, CT.[4] "Product Information. Amicar (aminocaproic acid)." Immunex Corporation, Seattle, WA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21152, 'Aminocaproic acid', 'Anti-inhibitor coagulant complex', 'Major', 'Due to additive or synergistic pharmacodynamic effects, the risk of thrombosis may be increased when antifibrinolytic agents (e.g., aminocaproic acid, aprotinin, tranexamic acid) are coadministered with other hemostatic agents such as anti-inhibitor coagulant complex, factor IX complex, or prothrombin complex.', NULL, 'Concomitant administration of antifibrinolytic agents with other hemostatic agents should generally be avoided.', NULL, 'Concomitant administration of antifibrinolytic agents with other hemostatic agents should generally be avoided. Consult the manufacturer''s product labeling for recommended dosing intervals between products.', 'Synergism', 'Eltrombopag, Romiplostim, Fostamatinib, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Phylloquinone, Lusutrombopag, Fibrinogen human, Antihemophilic factor, human recombinant, Coagulation Factor IX Human', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21167/', '[1] "Product Information. LYSTEDA (tranexamic acid)." Xanodyne Pharmaceuticals Inc, Newport, KY.[2] "Product Information. Amicar (aminocaproic acid)." Immunex Corporation, Seattle, WA.[3] "Product Information. Cyklokapron (tranexamic acid)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
