INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21158, 'Aminocaproic acid', 'Ticagrelor', 'Minor', 'Antifibrinolytic agents (e.g., aminocaproic acid or tranexamic acid) and recombinant factor VIIa may increase hemostasis and antagonize the effects of ticagrelor. They may be more effective in managing clinical bleeding events that occur during ticagrelor therapy compared to platelet infusions or desmopressin. No data exist regarding a hemostatic benefit of platelet transfusions in the presence of ticagrelor; circulating ticagrelor may inhibit transfused platelets. Template bleeding time did not decrease when ticagrelor was coadministered with desmopressin; therefore, desmopressin is unlikely to be effective in managing clinical bleeding events. Ticagrelor should be discontinued if active bleeding occurs, and may be resumed after the bleeding has been controlled and the cause identified.', NULL, '-', NULL, '-', 'Antagonism', 'Aprotinin', 'Betrixaban, Clopidogrel, Cilostazol, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Cangrelor, Dipyridamole, Caplacizumab, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21173/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
