INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21351, 'Amiodarone', 'Bisoprolol', 'Moderate', 'Additive effects of severe bradycardia, cardiac arrest, and ventricular fibrillation may occur in patients administered amiodarone and beta blockers. The mechanism may be related to additive slowing in AV conduction.', NULL, 'Clinical monitoring of patient hemodynamic status and response is recommended.', NULL, 'Clinical monitoring of patient hemodynamic status and response is recommended.', 'Synergism', 'Trandolapril, Moexipril', 'Propafenone, Procainamide, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21366/', '[1] Ogunyankin KO, Singh BN "Mortality reduction by antiadrenergic modulation of arrhythmogenic substrate: Significance of combining beta blockers and amiodarone." Am J Cardiol 84 (1999): r76-82[2] Boutitie F, Boissel JP, Connolly SJ, et al "Amiodarone interaction with beta-blockers - Analysis of the merged EMIAT (European Myocardial Infarct Amiodarone Trial) and CAMIAT (Canadian Amiodarone Myocardial Infarction Trial) databases." Circulation 99 (1999): 2268-75[3] Leor J, Levartowsky D, Sharon C, Farfel Z "Amiodarone and beta-adrenergic blockers: an interaction with metoprolol but not with atenolol." Am Heart J 16 (1988): 206-7[4] Warren R, Vohra J, Hunt D, Hamer A "Serious interactions of sotalol with amiodarone and flecainide." Med J Aust 152 (1990): 277[5] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[6] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amioda