INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21410, 'Amiodarone', 'Colesevelam', 'Moderate', 'INTERVAL: Colesevelam may decrease the gastrointestinal absorption of coadministered drugs. Approximately 20% to 40% reductions in systemic exposure (AUC) and 10% to 45% reductions in peak plasma concentration (Cmax) have been reported for cyclosporine, ethinyl estradiol, glimepiride, glyburide, levothyroxine, olmesartan, and sustained-release verapamil during coadministration with colesevelam.', NULL, 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose.', NULL, 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose. Clinical and laboratory monitoring of drug levels and effects should be considered.', 'Absorption', 'Fenofibric acid, Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Bempedoic acid, Alirocumab', 'Quinidine, Lidocaine, Dronedarone, Disopyramide, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21425/', '[1] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[5] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[6] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes Association." Diabetes Care 25(Suppl 1) (2002): S50-S60[7] "Product Information. Diabeta (glyburide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[8] Jerntorp P, Almer LO, Holin H, et al "Plasma chlorpropamide: a critical factor in chlorpropamide-alcohol flush." Eur J Clin Pharmacol 24 (1983): 237-42[9] Hartling SG, Faber OK, Wegmann ML, Wahlin-Boll E, Melander A "Interaction of ethanol and glipizide in humans." Diabetes Care 10 (1987): 683-6[10] Barnett AH, Spiliopoulos AJ, Pyke DA, et al "Metabolic studies in chlorpropamide-alcohol flush positive and negative type 2 (non-insulin dependent) diabetic patients with and without retinopathy." Diabetologia 24 (1983): 213-5[11] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[12] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21411, 'Amiodarone', 'Conivaptan', 'Major', 'Coadministration with conivaptan may significantly increase the plasma concentrations of certain antiarrhythmic agents such as amiodarone, lidocaine, and quinidine. The mechanism is conivaptan inhibition of CYP450 3A4 metabolism. The interaction has not been specifically studied but could conceivably lead to serious and/or life-threatening reactions including cardiac arrhythmias and other toxicities.', NULL, 'Caution is advised if conivaptan must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4 such as amiodarone, lidocaine, or quinidine.', NULL, 'Caution is advised if conivaptan must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4 such as amiodarone, lidocaine, or quinidine. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever conivaptan is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Metabolism', 'Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21426/', '[1] "Product Information. Vaprisol (conivaptan)." Cumberland Pharmaceuticals Inc, Nashville, TN.[2] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[3] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[4] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71[5] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21412, 'Amiodarone', 'Corticotropin', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with amiodarone. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Quinidine, Lidocaine, Dronedarone, Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, More', 'Mecasermin, Sermorelin, Tesamorelin, Thyrotropin alfa, Tetracosactide, Mecasermin rinfabate, Pegvisomant', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21427/', '[1] Antonelli D, Atar S, Freedberg NA, Rosenfeld T "Torsade de pointes in patients on chronic amiodarone treatment: contributing factors and drug interactions." Isr Med Assoc J 7 (2005): 163-5[2] "Product Information. Amiodarone Hydrochloride (amiodarone)." Bedford La