INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21661, 'Amiodarone', 'Nevirapine', 'Major', 'Coadministration with inducers of CYP450 3A4, such as rifamycins and St. John''s wort, may decrease the serum concentrations of amiodarone and its metabolite desethylamiodarone (DEA) and result in therapeutic failure. The proposed mechanism is induction of CYP450 3A4 hepatic metabolism of amiodarone.', NULL, 'Monitoring serum amiodarone and DEA levels and patient''s clinical status is recommended if CYP450 3A4 inducers are added to or deleted from a stable drug regimen that includes amiodarone.', NULL, 'Monitoring serum amiodarone and DEA levels and patient''s clinical status is recommended if CYP450 3A4 inducers are added to or deleted from a stable drug regimen that includes amiodarone. Alternative therapy or dosage adjustments may also be considered. Patients should be advised to notify their doctor if they experience worsening of their symptoms (e.g., palpitations or irregular heartbeat).', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Elbasvir, More', 'Propafenone, Tocainide, Procainamide, Flecainide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21676/', '[1] "Product Information. Cordarone Intravenous (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Zarembski DG, Fischer SA, Santucci PA, Porter MT, Costanzo MR, Trohman RG "Impact of rifampin on serum amiodarone concentrations in a patient with congenital heart disease." Pharmacotherapy 19 (1999): 249-51[3] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[4] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[5] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amiodarone tablets administered with and without food in healthy subjects." Am J Cardiol 87 (2001): 432-5[6] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[7] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21662, 'Amiodarone', 'Nilotinib', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Lidocaine, Moricizine, Bretylium, Tocainide', 'Regorafenib, Copanlisib, Alpelisib, Abemaciclib, Erdafitinib, Neratinib, Trametinib, Capmatinib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21677/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Yamreudeewong W, DeBisschop M, Martin L, Lower