INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21720, 'Amiodarone', 'Polatuzumab vedotin', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Quinidine, Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Pertuzumab, Cetuximab, Avelumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, Pembrolizumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21735/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online. Available from: URL: http://www.appco.com.au/appguide/default.asp." ([2006]):[3] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995): 201[4] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21721, 'Amiodarone', 'Polyethylene glycol (3350)', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21736/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21722, 'Amiodarone', 'Polyethylene glycol (3350 with electrolytes)', 'Major', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly. Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.', 'Synergism', 'Tocainide, Moricizine, Bretylium', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21737/', '[1] "Product Information. Suclear (polyethylene glycol 3350 with electrolytes)." Braintree Laboratories, Braintree, MA.[2] Hill AG, Parry BR "Hypokalaemia following bowel cleansing with sodium phosphate." N Z Med J 109 (1996): 347[3] "Product Information. Suprep Bowel Prep Kit (magnesium/potassium/sodium sulfates)." Braintree Laboratories, Braintree, MA.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. Fleet Phospho Soda (sodium biphosphate-sodium phosphate)." Fleet, CB, Lynchburg, VA.[7] "Product Information. Visicol (sodium biphosphate-sodium phosphate)." Salix Pharmaceuticals, Raleigh, NC.[8] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[9] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[10] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[11] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[12] "Product Information. Prepopik (citric acid/Mg oxide/Na picosulfate)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[13] "Product Information. Golytely (polyethylene glycol electrolyte solution)." Braintree, Braintree, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21723, 'Amiodarone', 'Polythiazide', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with amiodarone. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Quinidine, Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Ascorbic acid, Sulfadiazine, Lactic acid, Clindamycin, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, Furazolidone, Oxytetracycline, Zonisamide, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21738/', '[1] Antonelli D, Atar S, Freedberg NA, Rosenfeld T "Torsade de pointes in patients on chronic amiodarone treatment: contributing factors and drug interactions." Isr Med Assoc J 7 (2005): 163-5[2] "Product Information. Amiodarone Hydrochloride (amiodarone)." Bedford Laboratories, Bedford, OH.[3] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[4] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amiodarone tablets administered with and without food in healthy subjects." Am J Cardiol 87 (2001): 432-5[5] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21724, 'Amiodarone', 'Pomalidomide', 'Moderate', 'Coadministration with potent inhibitors of CYP450 1A2, CYP450 3A4, and P-glycoprotein may increase the plasma concentrations of pomalidomide, which has been shown to be primarily metabolized by these isoenzymes and also a substrate of the efflux transporter. Pomalidomide exposure is increased when given with a strong CYP450 1A2 inhibitor (e.g., fluvoxamine) in the presence of a strong CYP450 3A4 and P-gp inhibitor (e.g., ketoconazole). Coadministration with ketoconazole alone did not have a clinically significant effect on exposure to pomalidomide.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.', NULL, 'The use of pomalidomide with potent inhibitors of CYP450 1A2 (e.g., ciprofloxacin, fluvoxamine, tiabendazole) in the presence of strong CYP450 3A4 and P-gp inhibitors should generally be avoided. If coadministration is considered clinically necessary, the pomalidomide dose should be reduced by 50%. Dose reduction may also be required if pomalidomide is given with a strong inhibitor of CYP450 1A2 in the absence of a coadministered CYP450 3A4 and P-gp inhibitor. Patients should be monitored for occurrence of pomalidomide-related side effects, including nausea, diarrhea, and neutropenia.', 'Metabolism', 'Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, Upadacitinib, Apremilast, Inebilizumab, Cladribine, Belumosudil, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21739/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21725, 'Amiodarone', 'Ponatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ponatinib, which is a substrate of the isoenzyme.', NULL, 'Caution is advised if ponatinib is used in combination with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised if ponatinib is used in combination with moderate CYP450 3A4 inhibitors. Patients should be monitored for potentially increased side effects such as thromboembolism, ischemia, congestive heart failure, hypertension, hepatotoxicity, pancreatitis, myelosuppression, hemorrhage, and fluid retention.', 'Metabolism', 'Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Regorafenib, Copanlisib, Alpelisib, Abemaciclib, Erdafitinib, Neratinib, Trametinib, Capmatinib, Dacomitinib, Tivozanib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21740/', '[1] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21726, 'Amiodarone', 'Porfimer sodium', 'Moderate', 'Concomitant use of porfimer with drugs known to cause photosensitivity may increase the risk of a photosensitivity reaction. Photosensitivity with porfimer is due to residual drug being present in the skin. Patients treated with porfimer will be photosensitive for at least 30 days.', NULL, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', NULL, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', 'Synergism', 'Pertuzumab, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Enasidenib, Aflibercept, Trastuzumab, Hydroxyurea, Talimogene laherparepvec, Estramustine, More', 'Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21741/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Photofrin (porfimer)." Sanofi Winthrop Pharmaceuticals, New York, NY.[3] Blakely KM, Drucker AM, Rosen CF "Drug-induced photosensitivityan update: Culprit drugs, prevention and management." Drug Saf 42 (2019): 827-47[4] Hoffman GA, Gradl G, Schulz M, Haidinger G, Tanew A, Weber B "The frequency of photosensitizing drug dispensings in Austria and Germany: A correlation with their photosensitizing potential based on published literature." J Eur Acad Dermatol Venereol 34 (2020): 589-600[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21727, 'Amiodarone', 'Posaconazole', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of amiodarone. The proposed mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of amiodarone. The use of amiodarone has been associated with prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death. Within the azole class, ketoconazole and itraconazole are considered potent inhibitors, while fluconazole is comparatively weak and generally causes clinically significant interactions with CYP450 3A4 substrates only at dosages of 200 mg/day or more.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of amiodarone, concomitant use with azole antifungal agents should generally be avoided if possible. Otherwise, caution and close clinical monitoring are recommended, and patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', 'Moricizine, Tocainide, Bretylium, Mexiletine', 'Micafungin, Caspofungin, Oteseconazole, Flucytosine, Anidulafungin', 'Major', NULL, 'DDInter', 'pharmacodynam