INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21778, 'Amiodarone', 'Saquinavir', 'Major', 'Coadministration with saquinavir may significantly increase the plasma concentrations of some antiarrhythmic agents such as amiodarone, bepridil, flecainide, propafenone, and quinidine. The mechanism is decreased clearance due to inhibition of CYP450 3A4- and 2D6-mediated metabolism by saquinavir, particularly when coadministered with ritonavir as a pharmacokinetic booster. Although clinical data are lacking, the interaction could conceivably lead to serious and/or life-threatening reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', NULL, 'The use of saquinavir in combination with amiodarone, bepridil, flecainide, propafenone, and quinidine is considered contraindicated.', NULL, 'The use of saquinavir in combination with amiodarone, bepridil, flecainide, propafenone, and quinidine is considered contraindicated.', 'Metabolism', 'Tocainide, Bretylium, Mexiletine', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Elbasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21793/', '[1] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Fortovase (saquinavir)" Roche Laboratories, Nutley, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Kupfersc