INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (21807, 'Amiodarone', 'St. John''s Wort', 'Major', 'Coadministration with inducers of CYP450 3A4, such as rifamycins and St. John''s wort, may decrease the serum concentrations of amiodarone and its metabolite desethylamiodarone (DEA) and result in therapeutic failure. The proposed mechanism is induction of CYP450 3A4 hepatic metabolism of amiodarone.', NULL, 'Monitoring serum amiodarone and DEA levels and patient''s clinical status is recommended if CYP450 3A4 inducers are added to or deleted from a stable drug regimen that includes amiodarone.', NULL, 'Monitoring serum amiodarone and DEA levels and patient''s clinical status is recommended if CYP450 3A4 inducers are added to or deleted from a stable drug regimen that includes amiodarone. Alternative therapy or dosage adjustments may also be considered. Patients should be advised to notify their doctor if they experience worsening of their symptoms (e.g., palpitations or irregular heartbeat).', 'Metabolism', 'Quinidine, Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Esketamine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/21822/', '[1] "Product Information. Cordarone Intravenous (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Zarembski DG, Fischer SA, Santucci PA, Porter MT, Costanzo MR, Trohman RG "Impact of rifampin on serum amiodarone concentrations in a patient with congenital heart disease." Pharmacotherapy 19 (1999): 249-51[3] "Product In