INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10612, 'Afatinib', 'Ritonavir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of afatinib, which is a substrate of the efflux transporter.', NULL, 'Caution is advised if afatinib is used in combination with P-gp inhibitors.', NULL, 'Caution is advised if afatinib is used in combination with P-gp inhibitors. Patients should be monitored for potentially increased adverse effects such as diarrhea, which may lead to dehydration with or without renal impairment; cutaneous reactions including rash, erythema, and bullous, blistering, or exfoliating lesions; interstitial lung disease such as lung infiltration, pneumonitis, acute respiratory distress syndrome, and allergic alveolitis; hepatotoxicity, which may be life-threatening or fatal; keratitis characterized by acute or worsening eye inflammation, lacrimation, light sensitivity, blurred vision, eye pain, red eye, and/or ulceration; and left ventricular dysfunction. The manufacturer recommends reducing the daily dose of afatinib by 10 mg if not tolerated. The previous dose may be resumed after discontinuation of the P-gp inhibitor as tolerated.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10616/', '[1] "Product Information. Gilotrif (afatinib)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Gilotrif (afatinib)." Boehringer Ingelheim, Ridgefield, CT.[3] "Product Information. Gilotrif (afatinib)." Boehringer Ingelheim, Ridgefield, CT.[4] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365203);
