INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10657, 'Anastrozole', 'Ethinylestradiol', 'Moderate', 'Theoretically, estrogen-containing drugs may negate the pharmacologic effects of aromatase inhibitors in the treatment of hormone-dependent breast cancer. Aromatase inhibitors induce a state of estrogen deprivation in postmenopausal women by inhibiting the conversion of androgens to estrogens in adrenal and ovarian tissues, thus estrogen administration would be expected to reverse their therapeutic effects.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10661/', '[1] "Product Information. Arimidex (anastrozole)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Aromasin (exemestane)" Pharmacia & Upjohn, Kalamazoo, MI.[3] "Product Information. Femara (letrozole)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (10658, 'Anisindione', 'Ethinylestradiol', 'Moderate', 'Concomitant therapy with estrogen-containing drugs may diminish the therapeutic effects of oral anticoagulants. Estrogens can increase the plasma levels of certain clotting factors such as fibrinogen, prothrombin, and factors VII and VIII in a dose-dependent manner, resulting in increased risk of thromboembolism, stroke, and/or myocardial infarction. The risk may be further increased by lifestyle choices such as smoking and lack of exercise.', NULL, 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks.', NULL, 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks. Close clinical and laboratory monitoring are recommended if the combination is prescribed. Patients should be advised to promptly notify their physician if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/10662/', '[1] von Kaulla E, Droegemueller W, von Kaulla KN "Conjugated estrogens and hypercoagulability." Am J Obstet Gynecol 122 (1975): 688-92[2] Notelovitz M "Oral contraception and coagulation." Clin Obstet Gynecol 28 (1985): 73-83[3] Stangel JJ, Innerfield I, Reyniak JV, Stone ML "The effect of conjugated estrogens on coagulability in menopausal women." Obstet Gynecol 49 (1977): 314-6[4] Koch-Weser J, Sellers