    ) VALUES (1330089, 'Triazolam', 'Acetazolamide', 'Moderate', 'Benzodiazepines may counteract the beneficial effects of acetazolamide in preventing altitude sickness by inhibiting respiratory responses to hypoxia.', NULL, 'Caution is recommended if a CAI and a benzodiazepine must be used together.', NULL, 'Caution is recommended if a CAI and a benzodiazepine must be used together. Until more information is available, it may be prudent to avoid the concurrent use of triazolam and acetazolamide at extreme altitudes.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
