        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1330202, 'Insulin human (inhalation, rapid acting)', 'Acetylcysteine', 'Moderate', 'INTERVAL: Bronchodilators and other orally inhaled products may alter the absorption of inhaled human insulin. In nondiabetic subjects with mild asthma (n=36) and moderate asthma (n=31), administration of albuterol 30 minutes prior to inhaled human insulin resulted in a mean increase in insulin systemic exposure (AUC).', NULL, 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended.', NULL, 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended. Blood glucose should be monitored closely, and the insulin dose adjusted accordingly.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
