INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11002, 'Ethinylestradiol', 'Testolactone', 'Moderate', 'Theoretically, estrogen-containing drugs may negate the pharmacologic effects of aromatase inhibitors in the treatment of hormone-dependent breast cancer. Aromatase inhibitors induce a state of estrogen deprivation in postmenopausal women by inhibiting the conversion of androgens to estrogens in adrenal and ovarian tissues, thus estrogen administration would be expected to reverse their therapeutic effects.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', NULL, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', 'Antagonism', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Drospirenone, Etynodiol, Levonorgestrel, Desogestrel, Dienogest, Etonogestrel, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11006/', '[1] "Product Information. Arimidex (anastrozole)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Aromasin (exemestane)" Pharmacia & Upjohn, Kalamazoo, MI.[3] "Product Information. Femara (letrozole)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11003, 'Ethinylestradiol', 'Tetracycline', 'Moderate', 'RECOMMENDED: The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents. The non-hormonal placebo pills included in some oral contraceptive preparations may contain iron, usually ferrous fumarate. Concomitant administration of these iron pills may significantly decrease the gastrointestinal absorption of antibiotics such as quinolones and tetracyclines. The mechanism is chelation of the antibiotic by the iron cation, forming a complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.', NULL, 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy. In general, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after iron preparations to minimize the potential for interaction. The administration of tetracycline antibiotics and iron preparations should be separated by at least three to four hours, although this may not prevent the interaction with doxycycline. Concomitant use of doxycycline and iron preparations should be avoided if possible.', 'Absorption', 'Rifamycin, Chlorhexidine, Pantoprazole, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Nizatidine, Esomeprazole, More', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol ac