INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11050, 'Hydrocortisone', 'Aluminum hydroxide', 'Minor', 'Antacids and agents with acid-neutralizing effects may impair the absorption of dexamethasone, prednisolone, prednisone, and other corticosteroids, although data from published studies are somewhat conflicting. The mechanism of interaction and clinical significance are unknown. No particular intervention is necessary during concomitant therapy with these agents, but clinicians should be aware of the potential for interaction.', NULL, '-', NULL, '-', 'Absorption', 'Fluorometholone, Fluocinolone acetonide, Diclofenac, Amphotericin B, Miconazole, Metronidazole, Clotrimazole, Epinephrine, Chlorhexidine, Neomycin, Ketorolac, More', 'Magnesium carbonate', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11054/', '[1] Naggar VF, Khalil SA, Gouda MW "Effect of concomitant administration of magnesium trisilicate on GI absorption of dexamethasone in humans." J Pharm Sci 67 (1978): 1029-30[2] Tanner AR, Caffin JA, Halliday JW, Powell LW "Concurrent administration of antacids and prednisone: effect on serum levels of prednisolone." Br J Clin Pharmacol 7 (1979): 397-400[3] Albin H, Vincon G, Demotes-Mainard F, et al "Effect of aluminium phosphate on the bioavailability of cimetidine and prednisolone." Eur J Clin Pharmacol 26 (1984): 271-3', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11051, 'Hydrocortisone', 'Ambenonium', 'Moderate', 'Corticosteroids and adrenocorticotropic agents may diminish the therapeutic effects of acetylcholinesterase inhibitors in myasthenia gravis. The mechanism of interaction is unknown. Marked deterioration in muscle strength has been reported in patients with myasthenia gravis shortly after the initiation of corticosteroid therapy, particularly when high dosages were used.', NULL, 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis.', NULL, 'Corticosteroid therapy should be instituted at relatively low dosages (15 to 25 mg/day of prednisone or equivalent) and in a controlled setting in patients with myasthenia gravis. Respiratory support should be available, and the dosage should be increased stepwise as tolerated (approximately 5 mg/day of prednisone or equivalent at 2- to 3-day intervals until marked clinical improvement or a dosage of 50 mg/day is reached). Dose reductions of the acetylcholinesterase inhibitor may be required as symptoms improve, which often may be delayed and gradual.', 'Antagonism', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, More', 'Bethanechol, Pilocarpine, Cevimeline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11055/', '[1] Patten BM, Oliver KL, Engel WK "Adverse interaction between steroid hormones and anticholinesterase drugs." Neurology 24 (1974): 442-9[2] Brunner NG, Namba T, Grob D "Corticosteroids in management of severe, generalized myasthenia gravis." Neurology 22 (1972): 603-10[3] Namba T "Corticotropin therapy in patients with myasthenia gravis." Arch Neurol 26 (1972): 144-50[4] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[5] Millikan CH, Eaton LM "Clinical evaluation of ACTH and cortisone in myasthenia gravis." Neurology 1 (1951): 145-52', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11052, 'Hydrocortisone', 'Amifampridine', 'Moderate', 'The use of amifampridine is associated with a dose-related risk of seizures, and the risk may be further increased when coadministered with other drugs that can reduce the seizure threshold. Seizures have occurred in patients without a history of seizures taking amifampridine at recommended dosages and at various times after initiation of treatment.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.', NULL, 'Caution is advised if amifampridine is coadministered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin). Consider discontinuation or dose reduction of amifampridine in patients who experience a seizure during treatment. Use of amifampridine is contraindicated in patients with a history of seizures.', 'Synergism', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, More', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Deutetrabenazine, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11056/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] "Product Information. Firdapse (amifampridine)." Catalyst Pharmaceuticals, Inc., Coral Gables, FL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11053, 'Hydrocortisone', 'Amiloride', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Finerenone', 'Fluocinolone acetonide, Fluorometholone, Miconazole, Metronidazole, Clotrimazole, Epinephrine, Chlorhexidine, Neomycin, Fluocinolone acetonide, Salicylic acid, Fluorometholone, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11057/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11054, 'Hydrocortisone', 'Aminophylline', 'Moderate', 'The concomitant use of theophylline and corticosteroids may theoretically increase the risk of hypokalemia due to additive potassium-lowering effects. Additionally, theophylline serum concentrations may be altered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered.', NULL, 'Monitoring for altered efficacy and safety of theophylline and altered serum potassium and theophylline concentrations is advisable when these drugs are coadministered. Patients should be advised to notify their physician if they experience signs of hypokalemia (e.g., weakness, lethargy, and muscle pains or cramps), worsening respiratory symptoms, or signs of theophylline toxicity (e.g., nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat).', 'Synergism', 'Fluorometholone, Fluocinolone acetonide, Diclofenac, Miconazole, Clotrimazole, Epinephrine, Chlorhexidine, Neomycin, Salicylic acid, Ketorolac, Fluocinolone acetonide, More', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Oxtriphylline, Mepolizumab, Zafirlukast, Bromotheophylline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11058/', '[1] Fergusson RJ, Scott CM, Rafferty P, Gaddie J "Effect of prednisolone on theophylline pharmacokinetics in patients with chronic airflow obstruction." Thorax 42 (1987): 195-8[2] Buchanan N, Hurwitz S, Butler P "Asthma - a possible interaction between hydrocortisone and theophylline." S Afr Med J 56 (1979): 1147-8[3] Leavengood DC, Bunker-Soler AL, Nelson HS "The effect of corticosteroids on theophylline metabolism." Ann Allergy 50 (1983): 249-51[4] Elvey SM, Saccar CL, Rocci ML, Mansmann HC, Martynec DM, Kester MB "The effect of corticosteroids on theophylline metabolim in asthmatic children." Ann Allergy 56 (1986): 520[5] Tatsis G, Orphanidou D, Douratsos D, et al "The effect of steroids on theophylline absorption." J Int Med Res 19 (1991): 326-9[6] Anderson JL, Ayres JW, Hall CA "Potential pharmacokinetic interaction between theophylline and prednisone." Clin Pharm 3 (1984): 187-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11055, 'Hydrocortisone', 'Amiodarone', 'Major', 'Amiodarone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', NULL, 'Coadministration of amiodarone with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with amiodarone. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Indomethacin, Betamethasone, Fluocinolone acetonide, Fluorometholone, Tetracycline, Minocycline, Chlorhexidine, Neomycin, Salicylic acid, Ketorolac, Indomethacin, More', 'Propafenone, Tocainide, Procainamide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11059/', '[1] Antonelli D, Atar S, Freedberg NA, Rosenfeld T "Torsade de pointes in patients on chronic amiodarone treatment: contributing factors and drug interactions." Isr Med Assoc J 7 (2005): 163-5[2] "Product Information. Amiodarone Hydrochloride (amiodarone)." Bedford Laboratories, Bedford, OH.[3] Libersa CC, Brique SA, Motte KB, et al. "Dramatic inhibition of amiodarone metabolism induced by grapefruit juice." Br J Clin Pharmacol 49 (2000): 373-8[4] Meng X, Mojaverian P, Doedee M, Lin E, Weinryb I, Chiang ST, Kowey PR "Bioavailability of Amiodarone tablets administered with and without food in healthy subjects." Am J Cardiol 87 (2001): 432-5[5] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11056, 'Hydrocortisone', 'Amisulpride', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Indomethacin, Fluocinolone acetonide, Diclofenac, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Tetracycline, Minocycline, Miconazole, More', 'Mesoridazine, Methotrimeprazine, Asenapine, Perphenazine, Thiothixene, Pimavanserin, Lumateperone, Iloperidone, Triflupromazine, Loxapine, Molindone, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11060/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11057, 'Hydrocortisone', 'Amlodipine', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Ivabradine, Sacubitril, Nimodipine', 'Fluocinolone acetonide, Fluorometholone, Neomycin, Fluocinolone acetonide, Fluorometholone, Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluorometholone, Beclomethasone dipropionate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11061/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11058, 'Hydrocortisone', 'Amobarbital', 'Moderate', 'Barbiturates may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by barbiturates.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with a barbiturate may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Amphotericin B, Miconazole, Clotrimazole, Epinephrine, More', 'Suvorexant, Flurazepam, Methohexital, Zaleplon, Dichloralphenazone, Valerian, Chloral hydrate, Quazepam, Paraldehyde, Ramelteon, Lemborexant, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11062/', '[1] Stjernholm MR, Katz FH "Effects of diphenylhydantoin, phenobarbital, and diazepam on the metabolism of methylprednisolone and its sodium succinate." J Clin Endocrinol Metab 41 (1975): 887-93[2] Sehgal VN, Srivastava G "Corticosteroid-unresponsive pemphigus vulgaris following antiepileptic therapy." Int J Dermatol 27 (1988): 258[3] Brooks SM, Werk EE, Ackerman SJ, Sullivan I, Thrasher K "Adverse