INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11162, 'Hydrocortisone', 'Dofetilide', 'Major', 'Dofetilide can cause dose- and concentration-related QT interval prolongation. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', NULL, 'Caution is advised if dofetilide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised if dofetilide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with dofetilide. During therapy, potassium concentrations should be kept above 4 mEq/dL and magnesium concentrations above 1.8 mg/dL. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Propafenone, Tocainide, Procainamide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Indomethacin, Fluocinolone acetonide, Diclofenac, Betamethasone, Fluorometholone, Methylprednisolone, Tetracycline, Minocycline, Triamcinolone, Epinephrine, Chlorhexidine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11166/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11163, 'Hydrocortisone', 'Doxacurium', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants. The mechanism is unknown. In addition, the risk of myopathy may be increased after long-term use of neuromuscular blocking agents and corticosteroids.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy. Dose adjustments of the muscle relaxant may be required. The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Others', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Miconazole, Doxycycline, Metronidazole, Clotrimazole, More', 'Botulinum toxin type A', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11167/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] "Product Information. Norcuron (vecuronium)." Organon, West Orange, NJ.[3] Laflin MJ "Interaction of pancuronium and corticosteroids." Anesthesiology 47 (1977): 471-2[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Parr SM, Galletly DC, Robinson BJ "Betamethasone-induced resistance to vercuronium: a potential problem in neurosurgery." Anaesth Intensive Care 19 (1991): 103-5[6] "Product Infor