INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11261, 'Irbesartan', 'Hydrocortisone', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', 'Fluocinolone acetonide, Fluorometholone, Minocycline, Amphotericin B, Miconazole, Chlorhexidine, Neomycin, Fluocinolone acetonide, Fluorometholone, Mometasone furoate, Amcinonide, More', 'Sacubitril', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11265/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11262, 'Isavuconazonium', 'Hydrocortisone', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Doxycycline, Metronidazole, Epinephrine, Chlorhexidine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11266/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84', 1767