INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11367, 'Hydrocortisone', 'Pipecuronium', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants. The mechanism is unknown. In addition, the risk of myopathy may be increased after long-term use of neuromuscular blocking agents and corticosteroids.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', NULL, 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy. Dose adjustments of the muscle relaxant may be required. The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Others', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Miconazole, Doxycycline, Metronidazole, Clotrimazole, More', 'Botulinum toxin type A', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11371/', '[1] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[2] "Product Information. Norcuron (vecuronium)." Organon, West Orange, NJ.[3] Laflin MJ "Interaction of pancuronium and corticosteroids." Anesthesiology 47 (1977): 471-2[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Parr SM, Galletly DC, Robinson BJ "Betamethasone-induced resistance to vercuronium: a potential problem in neurosurgery." Anaesth Intensive Care 19 (1991): 103-5[6] "Product Information. Zemuron (rocuronium)." Organon, West Orange, NJ.[7] Meyers EF "Partial recovery from pancuronium neuromuscular blockade following hydrocortosone administration." Anesthesiology 46 (1977): 148-50', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11368, 'Hydrocortisone', 'Pirbuterol', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects. Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes. However, clinical data are limited, and the potential significance is unknown. Patients who are receiving systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or systemic corticosteroid therapy may be at a greater risk of developing hypokalemia.', NULL, '-', NULL, '-', 'Synergism', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Metronidazole, Chlorhexidine, More', 'Ephedrine, Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Olodaterol, Beclomethasone dipropionate', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11372/', '[1] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11369, 'Hydrocortisone', 'Piroxicam', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', NULL, 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', NULL, 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine, Tolazoline, Dimethyl sulfoxide, Fluocinolone acetonide, Cortisone, Hydrocortisone, Betamethasone, Fluorometholone', 'Fluocinolone acetonide, Fluorometholone, Tetracycline, Minocycline, Amphotericin B, Miconazole, Epinephrine, Chlorhexidine, Neomycin, Piroxicam, Fluocinolone acetonide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11373/', '[1] Wilcox CM, Shalek KA, Cotsonis G "Striking prevalence of over-the-counter nonsteroidal anti- inflammatory drug use in patients with upper gastrointestinal hemorrhage." Arch Intern Med 154 (1994): 42-6[2] Stewart JT, Pennington CR, Pringle R "Anti-inflammatory drugs and bowel perforations and haemorrhage." Br Med J 290 (1985): 787-8[3] Messer J, Reitman D, Sacks HS, et al "Association of adrenocorticosteroid therapy and peptic-ulcer disease." N Engl J Med 309 (1983): 21-4[4] Kaufman DW, Kelly JP, Sheehan JE, Laszlo A, Wiholm BE, Alfredsson L, Koff RS, Shapiro S "Nonsteroidal anti-inflammatory drug use in relation to major upper gastrointestinal bleeding." Clin Pharmacol Ther 53 (1993): 485-94[5] Buchman AL, Schwartz MR "Colonic ulceration associated with the systemic use of nonsteroidal antiinflammatory medication." J Clin Gastroenterol 22 (1996): 224-6[6] Sacanella E, Munoz F, Cardellach F, Estruch R, Miro O, Urbanomarquez A "Massive haemorrhage due to colitis secondary to nonsteroidal anti-inflammatory drugs." Postgrad Med J 72 (1996): 57-8[7] Levy M, Miller DR, Kaufman DW, Siskind V, Schwingl P, Rosenberg L, Strom B, Shapiro S "Major upper gastrointestinal tract bleeding. Relation to the use of aspirin and other nonnarcotic analgesics." Arch Intern Med 148 (1988): 281-5[8] Cantu TG, Lipani JA "Gastrointestinal ulceration with NSAIDs." Am J Med 99 (1995): 440-1[9] Thomas TP "The complications of systemic corticosteroid therapy in the elderly." Gerontology 30 (1984): 60-5[10] Piper JM, Ray WA, Daugherty JR, Griffin MR "Corticosteroid use and peptic ulcer disease: role of nonsteroidal ani-inflammatory drugs." Ann Intern Med 114 (1991): 735-40[11] ReMine SG, McIlrath DC "Bowel perforation in steroid-treated patients." Ann Surg 192 (1980): 581-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11370, 'Hydrocortisone', 'Pitolisant', 'Moderate', 'Concomitant use of pitolisant with NSAIDs and/or corticosteroids may increase the risk of gastrointestinal adverse effects such as dyspepsia, abdominal pain or discomfort, and gastritis due to potential additive irritant effects on the gastrointestinal mucosa. However, the effects were described as mostly mild to moderate in severity. Data are not available on the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients. If concomitant therapy is required, patients should be advised to report signs and symptoms of adverse GI effects, including abdominal pain or discomfort, dyspepsia, gastroesophageal reflux disease, gastritis, or the appearance of black, tarry stools.', 'Synergism', 'Fluocinolone acetonide, Fluorometholone, Tetracycline, Minocycline, Amphotericin B, Miconazole, Doxycycline, Clotrimazole, Epinephrine, Chlorhexidine, Neomycin, More', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Deutetrabenazine, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11374/', '[1] Cerner Multum, Inc. "UK 