INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11435, 'Hydrocortisone', 'Telaprevir', 'Moderate', 'Coadministration with telaprevir may increase the plasma concentrations of systemic corticosteroids. The mechanism involves telaprevir inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most steroids. No pharmacokinetic data are currently available, although telaprevir is a potent CYP450 3A4 inhibitor and may interact significantly.', NULL, 'The use of systemic corticosteroids in combination with telaprevir is not recommended.', NULL, 'The use of systemic corticosteroids in combination with telaprevir is not recommended. Systemic corticosteroids should not be used to treat telaprevir-induced rash.', 'Metabolism', 'Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac, Tetracycline, Acetylsalicylic acid, Minocycline, Amphotericin B, Doxycycline, Metronidazole, Epinephrine, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11439/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11436, 'Hydrocortisone', 'Telbivudine', 'Moderate', 'Prolonged use of certain nucleoside reverse transcriptase inhibitors may commonly cause myopathy, including rare cases of rhabdomyolysis. It is not known if the risk of myopathy is increased during concurrent administration of other agents associated with myopathy, including corticosteroids, fibric acid derivatives, HMG-CoA reductase inhibitors, chloroquine, hydroxychloroquine, penicillamine, and lipid-lowering doses of niacin.', NULL, 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together.', NULL, 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever.', 'Synergism', 'Fluocinolone acetonide, Fluorometholone, Tetracycline, Acetylsalicylic acid, Minocycline, Miconazole, Doxycycline, Clotrimazole, Epinephrine, Chlorhexidine, Fluocinolone acetonide, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11440/', '[1] "Product Information. Tyzeka (telbivudine)." IDEC Pharmaceuticals Corporation, San Diego, CA.[2] "Product Information. Retrovir (zidovudine)." Glaxo Wellcome, Research Triangle Park, NC.', 1767369485);
