INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11487, 'Hydrocortisone', 'Ziprasidone', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. While clinical data are lacking, the coadministration of ziprasidone and agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', NULL, 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with ziprasidone. Patients should be advised to notify their physician if they experience possible signs of an electrolyte imbalance, such as weakness, lethargy, drowsiness, confusion, muscle pains or cramps, dizziness, nausea, vomiting, tachycardia, or an irregular heartbeat.', 'Synergism', 'Mesoridazine, Methotrimeprazine, Asenapine, Perphenazine, Thiothixene, Pimavanserin, Lumateperone, Iloperidone, Triflupromazine, Loxapine, Molindone, More', 'Fluocinolone acetonide, Prednisolone, Betamethasone, Fluorometholone, Tetracycline, Minocycline, Prednisolone, Chlorhexidine, Neomycin, Salicylic acid, Ketorolac, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11491/', '[1] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11488, 'Alfentanil', 'Butalbital', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Nitrazepam, Zopiclone', 'Halothane, Isoflurane, Nitrous oxide, Desflurane, Propofol, Enflurane, Sevoflurane, Etomidate', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11492/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (11489, 'Aminophylline', 'Butalbital', 'Moderate', 'Barbiturates may decrease serum levels and therapeutic effects of the methylxanthines. The mechanism is barbiturate induction of CYP450 3A4 and 1A2 hepatic metabolism of methylxanthines.', NULL, 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together.', NULL, 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms.', 'Metabolism', 'Suvorexant, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Chloral hydrate, Paraldehyde, Ramelteon, Lemborexant, Ethchlorvynol, Zopiclone, More', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, Zafirlukast, Bromotheophylline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/11493/', '[1] Dahlqvist R, Steiner E, Koike Y, von Bahr C, Lind M, Billing B "Induction of theophylline metabolism by pentobarbital." Ther Drug Monit 11 (1989): 408-10[2] Bukowskyj M, Nakatsu K, Munt PW "Theophylline reassessed." Ann Intern Med 101 (1984): 63-73[3] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part I)." Clin Pharmacokinet 20 (1991): 66-80[4] Landay RA, Gonzalez MA, Taylor JC "Effect of phenobarbital on theophylline disposition." J Allergy Clin Immunol 62 (1978): 27-9', 1767369485);
