    ) VALUES (1330473, 'Alendronic acid', 'Ranitidine', 'Minor', 'Coadministration of alendronate and intravenous ranitidine has been shown to double the bioavailability of alendronate. The clinical significance of this increased bioavailability is unknown, as are the effects of oral and other intravenous H2-antagonists.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
