    ) VALUES (1330489, 'Cisatracurium', 'Ranitidine', 'Minor', 'Ranitidine may or may not decrease the effectiveness of neuromuscular blocking agents. Until more information is available, the clinician should be aware of the possibility of an interaction.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
