        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1330500, 'Doxacurium', 'Ranitidine', 'Minor', 'Ranitidine may or may not decrease the effectiveness of neuromuscular blocking agents. Until more information is available, the clinician should be aware of the possibility of an interaction.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
