    ) VALUES (1330526, 'Ranitidine', 'Lasmiditan', 'Moderate', 'Coadministration of lasmiditan and agents that lower heart rate may increase the risk of bradycardia.', NULL, 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk.', NULL, 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk. Consider monitoring heart rate in patients for whom the estimated decrease in heart rate may not be tolerated.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
