        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1330546, 'Ranitidine', 'Nelfinavir', 'Minor', 'There is currently no information in the medical literature regarding a potential interaction between nelfinavir and H2-receptor antagonists. Nelfinavir peak plasma concentration (Cmax), systemic exposure (AUC) and trough plasma concentration (Cmin) decreased by 37%, 36% and 39%, respectively.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
