        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1330564, 'Ranitidine', 'Posaconazole', 'Moderate', 'Coadministration with H2-receptor antagonists may decrease the systemic bioavailability of posaconazole from the oral suspension. The proposed mechanism is reduced solubility and absorption of posaconazole due to an increase in gastric pH induced by these agents.', NULL, 'Concomitant use of posaconazole oral suspension with cimetidine should generally be avoided.', NULL, 'Concomitant use of posaconazole oral suspension with cimetidine should generally be avoided. Until more information is available, this precaution should also be considered for other H2-receptor antagonists, although the manufacturer suggests otherwise. If possible, prescribers may consider suspending the H2 antagonist until completion of posaconazole therapy, or substituting an antifungal agent like fluconazole or voriconazole whose absorption is not affected by stomach pH. Delayed-release tablets of posaconazole may be used with H2 antagonists, as no pharmacokinetic interaction has been demonstrated with ranitidine or other agents affecting gastric pH such as proton pump inhibitors or antacids.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
