        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1330565, 'Ranitidine', 'Pramipexole', 'Minor', 'Drugs that are secreted by the renal tubules via the cationic transport system may decrease the oral clearance of pramipexole by approximately 20%. The mechanism is competitive inhibition of tubular secretion of pramipexole. This interaction is not likely to be of clinical importance, although it may be prudent to monitor the patient for increased adverse effects due to pramipexole.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
