        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1330566, 'Ranitidine', 'Prasugrel', 'Minor', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may modestly affect the pharmacokinetics of prasugrel, but does not appear to alter its antiplatelet effects. The proposed mechanism is delayed prasugrel dissolution due to increased gastric pH.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
