        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1330569, 'Ranitidine', 'Raltegravir', 'Minor', 'Since raltegravir solubility is increased at higher pH, coadministration with agents that elevate gastric pH may theoretically increase the plasma concentrations of raltegravir. However, concurrent use of proton pump inhibitors or H2-receptor antagonists in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with proton pump inhibitors or H2-receptor antagonists.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
