INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330628, 'Clopidogrel', 'Rabeprazole', 'Major', 'Coadministration with proton pump inhibitors (PPIs) may reduce the cardioprotective effects of clopidogrel. The proposed mechanism is PPI inhibition of the CYP450 2C19-mediated metabolic bioactivation of clopidogrel.', 'DDInter', 1767317475, 'Until further data are available, empiric use of proton pump inhibitors should preferably be avoided in patients treated with clopidogrel. PPIs should only be considered in high-risk patients such as those receiving dual antiplatelet therapy, those with a history of gastrointestinal bleeding or ulcers, and those receiving concomitant anticoagulant therapy, and then only after thorough assessment of risks versus benefits. If a PPI is required, dexlansoprazole, lansoprazole, or pantoprazole may be safer alternatives. Otherwise, H2-receptor antagonists or antacids should be prescribed whenever possible.', 'Metabolism', 'Until further data are available, empiric use of proton pump inhibitors should preferably be avoided in patients treated with clopidogrel.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330629, 'Conivaptan', 'Rabeprazole', 'Moderate', 'Coadministration with conivaptan may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by conivaptan.', 'DDInter', 1767317475, 'Caution is advised if conivaptan must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever conivaptan is added to or withdrawn from therapy, or the combination avoided altogether.', 'Metabolism', 'Caution is advised if conivaptan must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330630, 'Cyanocobalamin', 'Rabeprazole', 'Minor', 'By reducing or suppressing gastric acid secretion, H2-receptor antagonists and proton pump inhibitors may interfere with the gastrointestinal absorption of vitamin B12, a process that is dependent on the presence of gastric acid and pepsin. Clinical studies have shown that dietary (i.e., protein-bound) vitamin B12 malabsorption can occur during treatment with these agents, particularly proton pump inhibitors, although the likelihood of developing clinically significant deficiency over time is unknown.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
