INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330649, 'Flibanserin', 'Rabeprazole', 'Moderate', 'Coadministration with inhibitors of CYP450 2C19 may increase the plasma concentrations of flibanserin, which is partially metabolized by the isoenzyme. High plasma levels of flibanserin may increase the risk of severe hypotension, syncope, and central nervous system depression.', 'DDInter', 1767317475, 'Caution is advised when flibanserin is used with CYP450 2C19 inhibitors. Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', 'Caution is advised when flibanserin is used with CYP450 2C19 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330650, 'Fluconazole', 'Rabeprazole', 'Moderate', 'Coadministration with fluconazole may significantly increase the plasma concentrations of omeprazole and other proton pump inhibitors (PPIs). The proposed mechanism is fluconazole inhibition of PPI metabolism via CYP450 2C19 and 3A4.', 'DDInter', 1767317475, 'According to the manufacturers, dosage adjustment of omeprazole is not normally required when used with dual inhibitors of CYP450 2C19 and 3A4 such as fluconazole. However, it may be necessary in patients receiving higher dosages, such as those with Zollinger-Ellison syndrome. Patients should be monitored for potentially increased adverse effects of PPIs during coadministration with fluconazole. The same precaution may be applicable to other PPIs.', 'Metabolism', 'According to the manufacturers, dosage adjustment of omeprazole is not normally required when used with dual inhibitors of CYP450 2C19 and 3A4 such as fluconazole.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330651, 'Fluvoxamine', 'Rabeprazole', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of omeprazole and other proton pump inhibitors in a dose-dependent manner. The mechanism is fluvoxamine inhibition of the CYP450 2C19 metabolism of proton pump inhibitors.', 'DDInter', 1767317475, 'Although proton pump inhibitors are generally well tolerated, caution is advised if they are prescribed with fluvoxamine. Dosage adjustment should be considered in patients who experience excessive adverse effects such as drowsiness, blurred vision, tachycardia, nausea, vomiting, diaphoresis, flushing, headache, and dry mouth.', 'Metabolism', 'Although proton pump inhibitors are generally well tolerated, caution is advised if they are prescribed with fluvoxamine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330652, 'Fosamprenavir', 'Rabeprazole', 'Moderate', 'Coadministration with H2-receptor antagonists or proton pump inhibitors may decrease the oral bioavailability of amprenavir from its prodrug, fosamprenavir. It is possible that fosamprenavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance.', 'DDInter', 1767317475, 'Caution is advised if fosamprenavir is prescribed with H2-receptor antagonists or proton pump inhibitors. Antiretroviral response should be monitored closely during coadministration.', 'Absorption', 'Caution is advised if fosamprenavir is prescribed with H2-receptor antagonists or proton pump inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
