INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330662, 'Indinavir', 'Rabeprazole', 'Moderate', 'Proton pump inhibitors may decrease the gastrointestinal absorption of indinavir. The proposed mechanism is a pH-dependent reduction in drug dissolution or absorption. The interaction has been reported specifically with omeprazole but may be expected with other proton pump inhibitors based on their common pharmacologic action.', 'DDInter', 1767317475, 'Caution is advised during the chronic use of these drugs with indinavir due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of indinavir. Patients should be monitored for altered antiviral effectiveness.', 'Absorption', 'Caution is advised during the chronic use of these drugs with indinavir due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of indinavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330663, 'Iron protein succinylate', 'Rabeprazole', 'Moderate', 'The profound hypochlorhydria induced by proton pump inhibitors (PPIs) may impair the gastrointestinal absorption of nonheme iron, a process that is dependent on an acidic environment.', 'DDInter', 1767317475, 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors. If an interaction is suspected after ruling out other causes, it may be appropriate to discontinue the proton pump inhibitor or consider administering iron parenterally.', 'Absorption', 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330664, 'Itraconazole', 'Rabeprazole', 'Moderate', 'Proton pump inhibitors may decrease the gastrointestinal absorption of the azole antifungal agents, itraconazole and ketoconazole, both of which require an acidic environment for dissolution. By increasing gastric pH and reducing the acidity, proton pump inhibitors can decrease bioavailability of the azoles by 75% to 80%.', 'DDInter', 1767317475, 'If coadministration is necessary, an acidic pH may be produced with two capsules of glutamic acid hydrochloride administered 15 minutes before the azole dose. Administration with an acidic beverage such as Coca-Cola(R) or Pepsi(R) may also help. However, clinicians should still consider the possibility of a reduced or subtherapeutic antifungal effect. It may be appropriate to switch to an agent like fluconazole or terbinafine whose absorption is not affected by stomach pH.', 'Absorption', 'If coadministration is necessary, an acidic pH may be produced with two capsules of glutamic acid hydrochloride administered 15 minutes before the azole dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
