INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330667, 'Rabeprazole', 'Lapatinib', 'Moderate', 'Nonclinical data indicate that the solubility of lapatinib is pH-dependent. Therefore, long-term suppression of gastric acid secretion by H2-receptor antagonists or proton pump inhibitors may reduce lapatinib systemic exposure.', 'DDInter', 1767317475, 'Caution may be advisable if lapatinib is used in combination with H2-receptor antagonists or proton pump inhibitors. Patients should be monitored for potentially reduced therapeutic response to lapatinib.', 'Absorption', 'Caution may be advisable if lapatinib is used in combination with H2-receptor antagonists or proton pump inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330668, 'Rabeprazole', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', 'DDInter', 1767317475, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330669, 'Rabeprazole', 'Levothyroxine', 'Moderate', 'Concurrent administration of proton pump inhibitors (PPI) may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The mechanism of interaction is suspected to be PPI induced hypochlorhydria leading to reduced levothyroxine absorption since gastric acidity is an essential requirement for levothyroxine absorption.', 'DDInter', 1767317475, 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors. Consider the alteration in gastric pH caused by the PPI. Some authorities recommend separating administration of PPI and levothyroxine by several hours, however there are no studies showing improved absorption when PPIs are administered separately from levothyroxine. If concomitant administration is necessary, consider monitoring TSH level and watching for clinical evidence of reduced levothyroxine effects. Patients should be advised to contact their physician if they experience symptoms of hypothyroidism, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Absorption', 'Caution is advised if levothyroxine is used concomitantly with proton pump inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
