INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330710, 'St. John''s Wort', 'Rabeprazole', 'Moderate', 'Coadministration with potent inducers of CYP450 2C19 and/or 3A4, such as rifampin and St. John''s wort, may significantly decrease the plasma concentrations of drugs which are primarily metabolized by these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when potent inducers of CYP450 2C19 and/or 3A4 are used concomitantly with medications that are substrates of these CYP450 isoenzymes. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever St. John''s wort or rifampin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when potent inducers of CYP450 2C19 and/or 3A4 are used concomitantly with medications that are substrates of these CYP450 isoenzymes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330711, 'Stiripentol', 'Rabeprazole', 'Moderate', 'The concomitant administration of stiripentol may significantly increase plasma concentrations, pharmacologic effects, and toxicities of drugs that are substrates of CYP450 2C19, 3A4, and/or 2D6. The mechanism is inhibition of cytochrome P-450 metabolism by stiripentol.', 'DDInter', 1767317475, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use. Dosage adjustments may be required if increased plasma levels or effects are observed.', 'Metabolism', 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
