INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330741, 'Ethanol', 'Acitretin', 'Major', 'Ethanol consumption with acitretin leads to the formation of etretinate, which has a much longer half-life than acitretin. Major human fetal abnormalities have been associated with the administration of acitretin, etretinate, and other retinoids. The longer elimination half-life of etretinate relative to acitretin increases the duration of teratogenic potential for female patients.', 'DDInter', 1767317475, 'Female patients should be warned that ethanol is contraindicated during active treatment with acitretin and for two months after cessation of therapy.', 'Others', 'Female patients should be warned that ethanol is contraindicated during active treatment with acitretin and for two months after cessation of therapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1330742, 'Etonogestrel', 'Acitretin', 'Major', 'Acitretin interferes with the contraceptive effect of microdosed progestin preparations. The mechanism of interaction has not been described, and it is not known whether the effectiveness of implantable or injectable products are affected.', 'DDInter', 1767317475, 'Because use of acitretin is associated with major birth defects, microdosed ''minipill'' progestin preparations are not recommended for women of childbearing potential treated with acitretin. It is not known whether other progestational contraceptives such as implants and injectables offer adequate contraception during acitretin therapy. Simultaneous use of at least two methods of effective contraception is mandatory during acitretin therapy and for at least 3 years after discontinuing therapy. Input from a gynecologist or similar expert on adequate contraception should be sought as needed.', 'Antagonism', 'Because use of acitretin is associated with major birth defects, microdosed ''minipill'' progestin preparations are not recommended for women of childbearing potential treated with acitretin.', NULL, NULL, 'Major', 'pharmacodynamic');
